import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchMyApplications } from '../store/slices/applicationsSlice';
import { FiFileText, FiExternalLink } from 'react-icons/fi';
import DashboardLayout from '../components/DashboardLayout';
import { FILES_BASE_URL } from '../services/api';
import { createSelector } from '@reduxjs/toolkit';

// Memoized selector to avoid unnecessary rerenders
const selectApplications = createSelector(
  (state) => state.applications.myApplications,
  (apps) => apps
);

export default function MyApplications() {
  const dispatch = useDispatch();
  const user = useSelector((state) => state.auth.user);
  const apps = useSelector(selectApplications);
  const error = useSelector((state) => state.applications.error);
  const status = useSelector((state) => state.applications.status);

  // Fetch cover letter PDF
  const viewPDF = async (filename) => {
    try {
      const res = await fetch(`${FILES_BASE_URL}/files/coverLetter/${filename}`);
      if (!res.ok) throw new Error('File not found');
      const blob = await res.blob();
      const url = window.URL.createObjectURL(blob);
      window.open(url);
    } catch (err) {
      console.error('Failed to fetch PDF', err);
      alert('Could not load PDF. File may be missing.');
    }
  };

  // Fetch my applications after login
  useEffect(() => {
    if (user?.id) {
      dispatch(fetchMyApplications());
    }
  }, [dispatch, user]);

  const statusColor = (status) => {
    switch (status) {
      case 'reviewed':
        return 'text-yellow-700 bg-yellow-100';
      case 'accepted':
        return 'text-green-700 bg-green-100';
      case 'rejected':
        return 'text-red-700 bg-red-100';
      default:
        return 'text-gray-700 bg-gray-100';
    }
  };

  const formatDate = (dateStr) => (dateStr ? new Date(dateStr).toLocaleString() : '');

  return (
    <DashboardLayout>
      <div className="max-w-7xl mx-auto p-8">
        <h2 className="text-2xl font-bold mb-4">My Applications</h2>

        {status === 'loading' && <p className="text-gray-500 mb-4">Loading applications...</p>}
        {error && <p className="text-red-500 mb-4">{error}</p>}

        {apps.length === 0 && status !== 'loading' ? (
          <p className="text-gray-500">You haven't applied to any jobs yet.</p>
        ) : (
          <table className="w-full border-collapse border">
            <thead className="bg-gray-100">
              <tr>
                <th className="border px-4 py-2 text-left">Job Title</th>
                <th className="border px-4 py-2 text-left">Cover Letter</th>
                <th className="border px-4 py-2 text-left">CV Link</th>
                <th className="border px-4 py-2 text-left">Status</th>
                <th className="border px-4 py-2 text-left">Applied At</th>
                <th className="border px-4 py-2 text-left">Updated At</th>
              </tr>
            </thead>
            <tbody>
              {apps.map((a) => (
                <tr key={a.id} className="hover:bg-gray-50">
                  <td className="border px-4 py-2">{a.jobTitle || `Job ID: ${a.jobId}`}</td>
                  <td className="border px-4 py-2">
                    {a.coverLetter ? (
                      <button
                        onClick={() => viewPDF(a.coverLetter)}
                        className="text-blue-600 hover:underline flex items-center"
                      >
                        <FiFileText className="text-red-500 w-5 h-5 mr-1" />
                        View PDF
                      </button>
                    ) : (
                      <span className="text-gray-500">No file</span>
                    )}
                  </td>
                  <td className="border px-4 py-2">
                    {a.cvLink ? (
                      <a
                        href={a.cvLink}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-blue-600 hover:underline flex items-center"
                      >
                        View CV <FiExternalLink className="ml-1 w-4 h-4" />
                      </a>
                    ) : (
                      <span className="text-gray-500">No link</span>
                    )}
                  </td>
                  <td className={`border px-4 py-2 ${statusColor(a.status)} font-semibold`}>
                    {a.status.toUpperCase()}
                  </td>
                  <td className="border px-4 py-2">{formatDate(a.createdAt)}</td>
                  <td className="border px-4 py-2">{formatDate(a.updatedAt)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </DashboardLayout>
  );
}
