import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import api from '../../services/api';
import { FILES_BASE_URL } from '../../services/api';

// Fetch logged-in user applications
export const fetchMyApplications = createAsyncThunk(
  'applications/fetchMyApplications',
  async (_, { rejectWithValue }) => {
    try {
      const res = await api.get('/applications/mine');
      return res.data;
    } catch (err) {
      return rejectWithValue(err.response?.data || { message: err.message });
    }
  }
);

// Fetch a PDF cover letter
export const fetchCoverLetter = createAsyncThunk(
  'applications/fetchCoverLetter',
  async (filename, { rejectWithValue }) => {
    try {
      const res = await api.get(`/files/coverLetter/${filename}`, { responseType: 'blob' });
      const url = window.URL.createObjectURL(res.data);
      return url;
    } catch (err) {
      return rejectWithValue(err.response?.data || { message: err.message });
    }
  }
);

const slice = createSlice({
  name: 'applications',
  initialState: { myApplications: [], status: 'idle', error: null },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchMyApplications.fulfilled, (state, action) => {
        state.myApplications = action.payload;
        state.error = null;
      })
      .addCase(fetchMyApplications.rejected, (state, action) => {
        state.error = action.payload?.message || 'Failed to fetch applications';
      });
  }
});

export default slice.reducer;
