import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchJob } from '../store/slices/jobsSlice';
import { applyJob } from '../store/slices/applicationsSlice';
import { useDispatch, useSelector } from 'react-redux';

export default function JobDetails() {
  const { id } = useParams();
  const dispatch = useDispatch();
  const job = useSelector((s) => s.jobs.current);
  const auth = useSelector((s) => s.auth);
  const [cover, setCover] = useState('');
  const [cv, setCv] = useState('');
  const [msg, setMsg] = useState('');

  useEffect(() => {
    dispatch(fetchJob(id));
  }, [dispatch, id]);

  const apply = async () => {
    if (!auth.token) return setMsg('⚠️ You must log in to apply.');
    try {
      await dispatch(
        applyJob({ jobId: parseInt(id), coverLetter: cover, cvLink: cv })
      ).unwrap();
      setMsg('✅ Application submitted successfully!');
      setCover('');
      setCv('');
    } catch (e) {
      setMsg('❌ Error submitting application. Please try again.');
    }
  };

  if (!job)
    return (
      <div className="flex justify-center items-center min-h-screen text-gray-500">
        Loading job details...
      </div>
    );

  return (
    <div className="max-w-4xl mx-auto py-8 px-4">
      {/* Job Header */}
      <div className="bg-white shadow-md rounded-lg p-6 mb-6">
        <h2 className="text-3xl font-bold text-gray-800">{job.title}</h2>
        <p className="text-gray-600 text-sm mt-1">
          {job.company} • {job.location}
        </p>
        {job.salary && (
          <p className="text-green-600 font-semibold mt-1">{job.salary}</p>
        )}
      </div>

      {/* Job Description */}
      <div className="bg-white shadow-sm rounded-lg p-6 mb-6">
        <h3 className="text-xl font-semibold text-gray-700 mb-3">
          Job Description
        </h3>
        <p className="text-gray-600 leading-relaxed">{job.description}</p>
      </div>

      {/* Application Form */}
      <div className="bg-white shadow-lg rounded-lg p-6">
        <h3 className="text-xl font-semibold text-gray-700 mb-4">
          Apply for this job
        </h3>

        <textarea
          value={cover}
          onChange={(e) => setCover(e.target.value)}
          className="w-full border border-gray-300 rounded-lg px-3 py-2 mb-3 focus:outline-none focus:ring-2 focus:ring-indigo-400"
          placeholder="Cover letter"
          rows={4}
        />
        <input
          value={cv}
          onChange={(e) => setCv(e.target.value)}
          placeholder="CV link (Google Drive, Dropbox, etc.)"
          className="w-full border border-gray-300 rounded-lg px-3 py-2 mb-4 focus:outline-none focus:ring-2 focus:ring-indigo-400"
        />
        <button
          onClick={apply}
          className="bg-indigo-600 hover:bg-indigo-700 text-white font-semibold px-6 py-2 rounded-lg transition w-full md:w-auto"
        >
          Submit Application
        </button>

        {msg && (
          <div
            className={`mt-3 text-sm font-medium ${
              msg.includes('✅')
                ? 'text-green-600'
                : msg.includes('⚠️')
                ? 'text-yellow-600'
                : 'text-red-600'
            }`}
          >
            {msg}
          </div>
        )}
      </div>
    </div>
  );
}
