import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getApplicationsForJob, updateApplicationStatus } from '../store/slices/applicationsSlice';

export default function AdminApplications() {
  const { jobId } = useParams();
  const dispatch = useDispatch();
  const apps = useSelector((s) => s.applications.list);

  useEffect(() => {
    if (jobId) dispatch(getApplicationsForJob(jobId));
  }, [dispatch, jobId]);

  const setStatus = (id, status) =>
    dispatch(updateApplicationStatus({ id, status }));

  const statusColors = {
    pending: 'bg-yellow-100 text-yellow-700',
    reviewed: 'bg-blue-100 text-blue-700',
    accepted: 'bg-green-100 text-green-700',
    rejected: 'bg-red-100 text-red-700',
  };

  return (
    <div className="max-w-5xl mx-auto py-8 px-4">
      <h2 className="text-3xl font-bold text-gray-800 mb-6">
        Applications for Job #{jobId}
      </h2>

      {apps.length === 0 && (
        <p className="text-gray-500 text-sm">
          No applications found for this job.
        </p>
      )}

      <div className="space-y-4">
        {apps.map((a) => (
          <div
            key={a.id}
            className="bg-white shadow-sm rounded-lg p-5 border border-gray-100 hover:shadow-md transition"
          >
            <div className="flex justify-between items-center mb-3">
              <h3 className="text-lg font-semibold text-gray-800">
                {a.applicantEmail}
              </h3>
              <span
                className={`px-3 py-1 rounded-full text-xs font-semibold ${
                  statusColors[a.status] || 'bg-gray-100 text-gray-700'
                }`}
              >
                {a.status}
              </span>
            </div>

            <p className="text-gray-600 text-sm mb-4 whitespace-pre-line">
              {a.coverLetter}
            </p>

            {a.cvLink && (
              <a
                href={a.cvLink}
                target="_blank"
                rel="noopener noreferrer"
                className="text-indigo-600 hover:underline text-sm mb-4 block"
              >
                View CV
              </a>
            )}

            <div className="flex space-x-3">
              <button
                onClick={() => setStatus(a.id, 'reviewed')}
                className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-1 rounded-lg text-sm transition"
              >
                Mark Reviewed
              </button>
              <button
                onClick={() => setStatus(a.id, 'accepted')}
                className="bg-green-500 hover:bg-green-600 text-white px-4 py-1 rounded-lg text-sm transition"
              >
                Accept
              </button>
              <button
                onClick={() => setStatus(a.id, 'rejected')}
                className="bg-red-500 hover:bg-red-600 text-white px-4 py-1 rounded-lg text-sm transition"
              >
                Reject
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
