import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchMyApplications } from '../store/slices/myapplicationsSlice';
import DashboardLayout from '../components/DashboardLayout';

export default function MyApplications() {
  const dispatch = useDispatch();
  const { list: applications, status, error } = useSelector((s) => s.myApplications);

  useEffect(() => {
    dispatch(fetchMyApplications());
  }, [dispatch]);

  return (
    <DashboardLayout>
      <div className="max-w-7xl mx-auto py-12 px-4">
        <h2 className="text-3xl font-bold text-gray-800 mb-6">My Applications</h2>

        {/* Loading State */}
        {status === 'loading' && (
          <p className="text-gray-500">Loading applications...</p>
        )}

        {/* Error State */}
        {status === 'failed' && (
          <p className="text-red-500">Error: {error}</p>
        )}

        {/* Applications Table */}
        <div className="overflow-x-auto bg-white shadow-md rounded-lg">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Job Title</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Status</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Applied On</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-200">
              {applications.length === 0 && status === 'succeeded' && (
                <tr>
                  <td colSpan="3" className="text-center py-4 text-gray-500">
                    You haven't applied to any jobs yet.
                  </td>
                </tr>
              )}

              {applications.map((app) => (
                <tr key={app.id} className="hover:bg-gray-50 transition">
                  <td className="px-6 py-4 whitespace-nowrap">{app.jobTitle}</td>
                  <td className="px-6 py-4 whitespace-nowrap">{app.status}</td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    {new Date(app.createdAt).toLocaleDateString()}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </DashboardLayout>
  );
}
