// src/store/slices/myapplicationslices.js
import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import api from '../../services/api'; // axios instance with baseURL & auth token

// Thunk to fetch the logged-in user's applications
export const fetchMyApplications = createAsyncThunk(
  'applications/fetchMyApplications',
  async (_, { rejectWithValue }) => {
    try {
      const res = await api.get('/applications/mine'); // backend endpoint
      console.log('Fetched applications:', res.data);
      return res.data; // this should be the array from backend
    } catch (err) {
      return rejectWithValue(err.response?.data?.message || err.message);
    }
  }
);

const myApplicationsSlice = createSlice({
  name: 'applications',
  initialState: {
    myApplications: [],
    status: 'idle',
    error: null,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchMyApplications.pending, (state) => {
        state.status = 'loading';
        state.error = null;
      })
      .addCase(fetchMyApplications.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.myApplications = action.payload; // ✅ store array here
      })
      .addCase(fetchMyApplications.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.payload || 'Failed to load applications';
      });
  },
});

export default myApplicationsSlice.reducer;
