import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import api from '../../services/api';

// Fetch applications of the logged-in user
export const fetchMyApplications = createAsyncThunk(
  'applications/fetchMyApplications',
  async (_, { rejectWithValue }) => {
    try {
      const res = await api.get('/applications/mine'); // backend endpoint
      console.log('Fetched applications:', res.data);
      return res.data;
    } catch (err) {
      return rejectWithValue(err.response?.data || { message: err.message });
    }
  }
);

// Other async thunks (applyJob, getApplicationsForJob, updateApplicationStatus)
// should also be imported and handled here if needed

const slice = createSlice({
  name: 'applications',
  initialState: {
    myApplications: [], // logged-in user applications
    list: [],           // all applications (for admin)
    last: null,         // last submitted application
    status: 'idle',
    error: null,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      // Logged-in user's applications
      .addCase(fetchMyApplications.fulfilled, (state, action) => {
        state.myApplications = action.payload;
        state.error = null;
      })
      .addCase(fetchMyApplications.rejected, (state, action) => {
        state.error = action.payload?.message || 'Failed to fetch applications';
      });

    
  },
});

export default slice.reducer;
