import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchMyApplications } from '../store/slices/myapplicationslices';
import DashboardLayout from '../components/DashboardLayout';
import { Link } from 'react-router-dom';

export default function MyApplications() {
  const dispatch = useDispatch();
  const { list, status, error } = useSelector((state) => state.myApplications);

  useEffect(() => {
    dispatch(fetchMyApplications());
  }, [dispatch]);

  // Status color map
  const statusColor = (status) => {
    switch (status?.toLowerCase()) {
      case 'reviewed':
        return 'text-yellow-700 bg-yellow-100';
      case 'accepted':
        return 'text-green-700 bg-green-100';
      case 'rejected':
        return 'text-red-700 bg-red-100';
      default:
        return 'text-gray-700 bg-gray-100';
    }
  };
  

  const getStatusColor = (statusText) =>
    statusColors[statusText] || 'bg-gray-100 text-gray-800';

  return (
    <DashboardLayout>
      <div className="max-w-7xl mx-auto py-12 px-4">
        <h2 className="text-3xl font-bold text-gray-800 mb-6">My Applications</h2>

        {/* Loading & Error */}
        {status === 'loading' && (
          <div className="text-center py-6 text-gray-500">Loading applications...</div>
        )}
        {error && (
          <div className="bg-red-100 text-red-700 p-4 rounded-md mb-6">
            Error: {error}
          </div>
        )}

        {/* Table */}
        <div className="overflow-x-auto bg-white shadow-md rounded-lg">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Job Title
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Company
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Applied On
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Action
                </th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-200">
              {list.length === 0 && status !== 'loading' && (
                <tr>
                  <td colSpan="5" className="text-center py-4 text-gray-500">
                    No applications found.
                  </td>
                </tr>
              )}

              {list.map((app) => (
                <tr key={app.id} className="hover:bg-gray-50 transition">
                  <td className="px-6 py-4 whitespace-nowrap">{app.jobTitle}</td>
                  <td className="px-6 py-4 whitespace-nowrap">{app.company}</td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span
                      className={`px-3 py-1 text-sm rounded-full font-medium ${getStatusColor(
                        app.status
                      )}`}
                    >
                      {app.status}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    {new Date(app.appliedAt).toLocaleDateString()}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <Link
                      to={`/jobs/${app.jobId}`}
                      className="text-indigo-600 hover:underline font-medium"
                    >
                      View Job
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </DashboardLayout>
  );
}
